mapper.select:
select b.*, author.name as authorName, c.name as classifyName from blog b join user as author on author.id = b.author_id join classify as c on c.id = b.classify_id
and b.title like concat('%', #{title}, '%')
and b.classify_id = #{classifyId}
and b.id in (select blog_id from blog_tag where tag_id = #{tagId})
and b.status = #{status}

mapper.update:
update blog
status = #{status}, publish_time = #{publishTime},
where id = #{id}

update blog
title = #{title}, types = #{types}, content = #{content}, classify_id = #{classifyId}, avatar = #{avatar}, description = #{description}, status = #{status},
where id = #{id}
